<template>
	<view class="tui-numberbox">
		<view
			class="tui-numbox-icon tui-icon-reduce "
			:class="[disabled || min >= inputValue ? 'tui-disabled' : '']"
			@tap="reduce"
			:style="{ color: iconColor, fontSize: iconSize + 'rpx' }"
		></view>
		<input
			type="number"
			v-model="inputValue"
			:disabled="disabled"
			@blur="blur"
			class="tui-num-input"
			:style="{ color: color, fontSize: size + 'rpx', backgroundColor: backgroundColor, height: height + 'rpx', minHeight: height + 'rpx', width: width + 'rpx' }"
		/>
		<view
			class="tui-numbox-icon tui-icon-plus"
			:class="[disabled || inputValue >= max ? 'tui-disabled' : '']"
			@tap="plus"
			:style="{ color: iconColor, fontSize: iconSize + 'rpx' }"
		></view>
	</view>
</template>

<script>
export default {
	name: 'tuiNumberbox',
	props: {
		value: {
			type: Number,
			default: 1
		},
		//最小值
		min: {
			type: Number,
			default: 1
		},
		//最大值
		max: {
			type: Number,
			default: 99
		},
		//迈步大小 1 1.1 10...
		step: {
			type: Number,
			default: 1
		},
		//是否禁用操作
		disabled: {
			type: Boolean,
			default: false
		},
		//加减图标大小 rpx
		iconSize: {
			type: Number,
			default: 26
		},
		iconColor: {
			type: String,
			default: '#666666'
		},
		//input 高度
		height: {
			type: Number,
			default: 42
		},
		//input 宽度
		width: {
			type: Number,
			default: 80
		},
		size: {
			type: Number,
			default: 28
		},
		//input 背景颜色
		backgroundColor: {
			type: String,
			default: '#F5F5F5'
		},
		//input 字体颜色
		color: {
			type: String,
			default: '#333'
		},
		//索引值，列表中使用
		index: {
			type: [Number, String],
			default: 0
		},
		//自定义参数
		custom: {
			type: [Number, String],
			default: 0
		}
	},
	created() {
		this.inputValue = +this.value;
	},
	data() {
		return {
			inputValue: 0
		};
	},
	watch: {
		value(val) {
			this.inputValue = +val;
		}
	},
	methods: {
		getScale() {
			let scale = 1;
			//浮点型
			if (!Number.isInteger(this.step)) {
				scale = Math.pow(10, (this.step + '').split('.')[1].length);
			}
			return scale;
		},
		calcNum: function(type) {
			if (this.disabled) {
				return;
			}
			const scale = this.getScale();
			let num = this.inputValue * scale;
			let step = this.step * scale;
			if (type === 'reduce') {
				num -= step;
			} else if (type === 'plus') {
				num += step;
			}
			let value = num / scale;
			if (type === 'plus' && value < this.min) {
				value = this.min;
			} else if (type === 'reduce' && value > this.max) {
				value = this.max;
			}
			if (value < this.min || value > this.max) {
				return;
			}
			this.handleChange(value, type);
		},
		plus: function() {
			this.calcNum('plus');
		},
		reduce: function() {
			this.calcNum('reduce');
		},
		blur: function(e) {
			let value = e.detail.value;
			if (value) {
				if (~value.indexOf('.') && Number.isInteger(this.step)) {
					value = value.split('.')[0];
				}
				value = Number(value);
				if (value > this.max) {
					value = this.max;
				} else if (value < this.min) {
					value = this.min;
				}
			} else {
				value = this.min;
			}
			if ((value == this.value && value != this.inputValue) || !e.detail.value) {
				this.inputValue = value;
			}
			this.handleChange(value, 'blur');
		},
		handleChange(value, type) {
			if (this.disabled) return;
			this.$emit('change', {
				value: value,
				type: type,
				index: this.index,
				custom: this.custom
			});
		}
	}
};
</script>

<style scoped>
@font-face {
	font-family: 'numberbox';
	src: url(data:application/font-woff;charset=utf-8;base64,)
		format('woff');
	font-weight: normal;
	font-style: normal;
}

.tui-numbox-icon {
	font-family: 'numberbox' !important;
	font-style: normal;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
	padding: 10rpx;
}

.tui-icon-reduce:before {
	content: '\e691';
}

.tui-icon-plus:before {
	content: '\e605';
}

.tui-numberbox {
	display: -webkit-inline-flex;
	display: inline-flex;
	align-items: center;
}

.tui-num-input {
	text-align: center;
	margin: 0 12rpx;
	font-weight: 400;
}

.tui-disabled {
	color: #ededed !important;
}
</style>
